INSERT [dbo].[Templates] ([TemplateId], [TemplateName], [TemplateKey], [TemplateSubject], [TemplateContent], [IsActive], [CreatedDate], [CreatedBy], [UpdatedDate], [UpdatedBy], [IsDeleted]) VALUES (1, N'Eligibility Email', N'EligibilityEmail', N'Inviting Applications for #SchemeName# ', N'<p><span style="font-size:11.0pt;font-family:&quot;Calibri&quot;,sans-serif;  mso-fareast-font-family:Calibri;mso-fareast-theme-font:minor-latin;color:black;  mso-ansi-language:EN-IN;mso-fareast-language:EN-IN;mso-bidi-language:AR-SA"><!--[if gte vml 1]><v:shapetype   id="_x0000_t75" coordsize="21600,21600" o:spt="75" o:preferrelative="t"   path="m@4@5l@4@11@9@11@9@5xe" filled="f" stroked="f">   <v:stroke joinstyle="miter"/>   <v:formulas>    <v:f eqn="if lineDrawn pixelLineWidth 0"/>    <v:f eqn="sum @0 1 0"/>    <v:f eqn="sum 0 0 @1"/>    <v:f eqn="prod @2 1 2"/>    <v:f eqn="prod @3 21600 pixelWidth"/>    <v:f eqn="prod @3 21600 pixelHeight"/>    <v:f eqn="sum @0 0 1"/>    <v:f eqn="prod @6 1 2"/>    <v:f eqn="prod @7 21600 pixelWidth"/>    <v:f eqn="sum @8 21600 0"/>    <v:f eqn="prod @7 21600 pixelHeight"/>    <v:f eqn="sum @10 21600 0"/>   </v:formulas>   <v:path o:extrusionok="f" gradientshapeok="t" o:connecttype="rect"/>   <o:lock v:ext="edit" aspectratio="t"/>  </v:shapetype><v:shape id="Picture_x0020_13" o:spid="_x0000_i1025" type="#_x0000_t75"   alt="" style=''width:585pt;height:127.5pt''>   <v:imagedata src="file:///C:/Users/amit/AppData/Local/Temp/msohtmlclip1/01/clip_image001.jpg"    o:href="cid:image003.jpg@01D98C9C.FC32BA30"/>  </v:shape><![endif]--></span><img src="data:image/jpeg;base64," data-filename="Inviting Employees.jpg" style="width: 630.125px; height: 137.333px;"><span style="font-size:11.0pt;font-family:&quot;Calibri&quot;,sans-serif;  mso-fareast-font-family:Calibri;mso-fareast-theme-font:minor-latin;color:black;  mso-ansi-language:EN-IN;mso-fareast-language:EN-IN;mso-bidi-language:AR-SA"><!--[if !vml]--></span></p><table class="MsoNormalTable" border="0" cellspacing="0" cellpadding="0" style="background-image: initial; background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial;">   <tbody><tr style="mso-yfti-irow:0;mso-yfti-firstrow:yes;height:1.0pt">    <td width="630" valign="top" style="width:472.7pt;border-top:none;border-left:    solid windowtext 1.0pt;border-bottom:none;border-right:solid windowtext 1.0pt;    padding:0cm 5.4pt 0cm 5.4pt;height:1.0pt">    <p class="MsoNormal" style="text-align:justify;line-height:115%"><span style="font-size:12.0pt;line-height:115%;color:black">Dear Colleagues,<o:p></o:p></span></p>    <p class="MsoNormal" style="text-align:justify;line-height:115%"><span style="color: black; font-size: 12pt;">Greetings from MSTA!</span></p>    <p class="MsoNormal" style="text-align:justify;line-height:115%"><span style="font-size: 12pt; line-height: 115%; color: black;">In line with the </span><span lang="EN" style="font-size: 12pt; line-height: 115%; color: black;">objective of building future leaders with superior management capabilities, </span><span style="font-size: 12pt; line-height: 115%; color: black;">Higher Education    Schemes are offered to eligible employees. Through this scheme, employees can    enroll in&nbsp;</span><span style="font-size: 1rem; text-align: left;">#SchemeName#</span><span style="font-size: 12pt; line-height: 115%; color: black;">&nbsp;</span><span style="font-size: 12pt; line-height: 115%; color: black;">Program from below partner institutes-</span></p>    <p class="MsoNormal" style="text-align:justify;line-height:115%"><o:p>#InstituteNameSeatList#</o:p></p><p class="MsoNormal" style="text-align: left; line-height: 115%;"><span style="font-size: 12pt; color: black;">As per scheme    eligibility &amp; selection criteria, we are pleased to invite applications    for&nbsp;</span><span style="font-size: 1rem; text-align: left;">#SchemeName#&nbsp;</span><span style="font-size: 12pt; color: black;">program&nbsp;by&nbsp;</span><span style="text-align: left;"><span style="font-size: 1rem;"></span>#InstituteList#<span style="font-size: 1rem;">&nbsp;</span></span><span style="font-size: 12pt; color: black;">from employees    working in Delhi/NCR/Rohtak offices. All eligible employees are marked in Bcc    of this mail.</span><br></p>    <p class="MsoNormal" style="text-align:justify;line-height:115%"><span style="color: black; font-size: 12pt; text-align: left;">In addition to    the above, employees also need to fulfill the below eligibility criteria as    required by the institutes:</span></p>    <ol style="margin-top:0cm" start="1" type="a"><li class="MsoNormal" style="line-height:105%;mso-list:l1 level1 lfo2"><span style="font-size:12.0pt;line-height:105%;mso-fareast-font-family:&quot;Times New Roman&quot;;         color:black">The applicants must have 50% marks or equivalent CGPA in         both class X and XII board exams</span><span style="font-size:14.0pt;         line-height:105%;mso-fareast-font-family:&quot;Times New Roman&quot;"><o:p></o:p></span></li>     <li class="MsoNormal" style="line-height:105%;mso-list:l1 level1 lfo2"><span style="font-size:12.0pt;line-height:105%;mso-fareast-font-family:&quot;Times New Roman&quot;;         color:black">The Applicants must have a minimum 3-year Bachelor''s Degree,         with at least 50% marks or equivalent CGPA in any discipline from any         University, recognized by the Ministry of HRD, Government of India. </span><span style="font-size:14.0pt;line-height:105%;mso-fareast-font-family:&quot;Times New Roman&quot;"><o:p></o:p></span></li>     <li class="MsoNormal" style="line-height:105%;mso-list:l1 level1 lfo2"><span style="font-size:12.0pt;line-height:105%;mso-fareast-font-family:&quot;Times New Roman&quot;;         color:black">Minimum post-qualification work experience:<br>         - For #InstituteList# </span><span style="font-size:12.0pt;line-height:105%;mso-fareast-font-family:&quot;Times New Roman&quot;;         color:black">: 3 years as on 30<sup>th</sup> April 2023</span></li>    </ol>    <p class="MsoNormal" style="text-align:justify;line-height:115%"><span style="color: black; font-size: 12pt;">Please refer to the    attached brochures for further details.</span><br></p>    <p class="MsoNormal" style="line-height:115%"><span style="color: black; font-size: 12pt;">All interested employees who fulfill the above    eligibility criteria may please fill out the attached application form, get    approval from their respective Department Head and Divisional Head, and send the    same to </span><b style="color: black; font-size: 12pt;">Mr. Aakash Sharma, GTS Department (<a href="mailto:prateek.dave@maruti.co.in">aakash.sharma@maruti.co.in</a>)</b><span style="color: black; font-size: 12pt;">.</span><br></p> <p style="font-weight: 700"> Please click on the below link: <a href="http://techefficio.centralindia.cloudapp.azure.com/Education/Nomination/Employee">http://techefficio.centralindia.cloudapp.azure.com/Education/Nomination/Employee</a></p>  <p class="MsoNormal" style="text-align:justify;line-height:115%"><span style="color: black; font-size: 12pt;">The timelines are as    below:</span><br></p>    <p class="MsoNormal" style="text-align:justify;line-height:115%"><br></p>    <table class="MsoNormalTable" border="0" cellspacing="0" cellpadding="0">     <tbody><tr style="mso-yfti-irow:0;mso-yfti-firstrow:yes;height:14.7pt">      <td width="370" valign="top" style="width:277.25pt;border:solid windowtext 1.0pt;      padding:0cm 5.4pt 0cm 5.4pt;height:14.7pt">      <p class="MsoNormal" style="text-align:justify;line-height:105%"><span style="font-size:12.0pt;line-height:105%;color:black">Last Date for      submitting</span><span style="font-size:12.0pt;line-height:105%">&nbsp;an approved <span style="color:black">nomination</span> form<span style="color:black"><o:p></o:p></span></span></p>      </td>      <td width="208" valig