INSERT [dbo].[Templates] ([TemplateId], [TemplateName], [TemplateKey], [TemplateSubject], [TemplateContent], [IsActive], [CreatedDate], [CreatedBy], [UpdatedDate], [UpdatedBy], [IsDeleted]) VALUES (3, N'Admission Email', N'AdmissionEmail', N'Result Announcement #InstituteName# Higher Education Scheme for #SchemeName#', N'<p><img src="data:image/jpeg;base64," data-filename="Inviting Employees.jpg" style="width: 780px;"></p><table class="MsoNormalTable" border="0" cellspacing="0" cellpadding="0" align="left" style="background-image: initial; background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial; margin-left: 7.5pt; margin-right: 7.5pt;">   <tbody><tr style="mso-yfti-irow:0;mso-yfti-firstrow:yes;height:53.75pt">    <td width="784" valign="top" style="width:587.8pt;border-top:none;border-left:    solid windowtext 1.0pt;border-bottom:none;border-right:solid windowtext 1.0pt;    padding:0cm 5.4pt 0cm 5.4pt;height:53.75pt">    <p class="MsoNormal"><span style="font-size:12.0pt;color:black">Dear    Colleagues,</span><span style="font-size:12.0pt"><o:p></o:p></span></p>    <p class="MsoNormal"><span style="font-size:12.0pt">&nbsp;</span></p>    <p class="MsoNormal" style="text-align:justify;line-height:150%;mso-element:    frame;mso-element-frame-hspace:9.75pt;mso-element-wrap:around;mso-element-anchor-vertical:    paragraph;mso-element-anchor-horizontal:column;mso-height-rule:exactly"><span style="font-size:12.0pt;line-height:150%;color:black">We are pleased to    announce the result for admission at&nbsp;</span><span style="font-size: 1rem; text-align: left;">#InstituteName#</span><span style="color: black; font-size: 12pt;">.</span></p><p class="MsoNormal" style="text-align:justify;line-height:150%;mso-element:    frame;mso-element-frame-hspace:9.75pt;mso-element-wrap:around;mso-element-anchor-vertical:    paragraph;mso-element-anchor-horizontal:column;mso-height-rule:exactly"><span style="font-size:12.0pt;line-height:150%;color:black">    Heartiest congratulations to the successful participants!</span><span style="font-size:12.0pt;line-height:150%;color:#1F497D"><br>    </span><span style="font-size:12.0pt;line-height:150%;color:black">The list    of successful employees is as below:</span></p><p class="MsoNormal" style="text-align:justify;line-height:150%;mso-element:    frame;mso-element-frame-hspace:9.75pt;mso-element-wrap:around;mso-element-anchor-vertical:    paragraph;mso-element-anchor-horizontal:column;mso-height-rule:exactly"><span style="font-size:12.0pt;line-height:150%;color:black">#Meritlist#</span></p>    <p class="MsoNormal" style="line-height:105%;mso-element:frame;mso-element-frame-hspace:    9.75pt;mso-element-wrap:around;mso-element-anchor-vertical:paragraph;    mso-element-anchor-horizontal:column;mso-height-rule:exactly"><span style="font-size:12.0pt;line-height:105%;color:black">We request all&nbsp;selected    employees to send their confirmation (through the attached format) towards    acceptance of this admission offer&nbsp;to&nbsp;Mr. Aakash Sharma (</span><span style="color:black"><a href="mailto:aakash.sharma@maruti.co.in">aakash.sharma@maruti.co.in</a></span><span style="font-size:12.0pt;line-height:105%;color:black">) latest by <b>14<sup>th</sup>    July 2023. </b>This year, we have declared the results together so that the    employees who have cleared the selection process of both institutes can make    an informed decision.&nbsp; <o:p></o:p></span></p> <p style="font-weight: 700">Please click on the link to confirm on the accpetance of admission:<a href="http://techefficio.centralindia.cloudapp.azure.com/Education/Admission/Employee"> http://techefficio.centralindia.cloudapp.azure.com/Education/Admission/Employee </a></p>  <p class="MsoNormal" style="line-height:105%;mso-element:frame;mso-element-frame-hspace:    9.75pt;mso-element-wrap:around;mso-element-anchor-vertical:paragraph;    mso-element-anchor-horizontal:column;mso-height-rule:exactly"><span style="color: black; font-size: 12pt;">The details of waitlist    employees as per merit are given as below:</span></p>    <p class="MsoNormal" style="line-height:105%;mso-element:frame;mso-element-frame-hspace:    9.75pt;mso-element-wrap:around;mso-element-anchor-vertical:paragraph;    mso-element-anchor-horizontal:column;mso-height-rule:exactly"><o:p>&nbsp;</o:p><span style="font-size: 1rem;">#Waitlist#</span></p>    <p class="MsoNormal" style="line-height:150%;mso-element:frame;mso-element-frame-hspace:    9.75pt;mso-element-wrap:around;mso-element-anchor-vertical:paragraph;    mso-element-anchor-horizontal:column;mso-height-rule:exactly"><span style="font-size:12.0pt;line-height:150%;color:black">Final admission will be    subject to documents screening and clearance by the institute. <br>    For further assistance, may please contact Mr. Aakash Sharma, (</span><span style="color:black"><a href="mailto:aakash.sharma@maruti.co.in">aakash.sharma@maruti.co.in</a></span><span style="font-size:12.0pt;line-height:150%;color:#1F497D">, </span><span style="font-size:12.0pt;line-height:150%;color:black">+91-9711279611).</span><span style="font-size:12.0pt;line-height:150%"><o:p></o:p></span></p> <p class="MsoNoSpacing" style="margin-left:0cm;mso-element:frame;mso-element-frame-hspace:    9.75pt;mso-element-wrap:around;mso-element-anchor-vertical:paragraph;    mso-element-anchor-horizontal:column;mso-height-rule:exactly"><span style="font-size:12.0pt;color:black">We wish all</span><span style="font-size:12.0pt;color:#1F497D"> </span><span style="font-size:12.0pt;    color:black">the best to the successful employees for their academic journey    ahead.</span><span style="font-size:12.0pt;color:windowtext;mso-color-alt:    windowtext"><o:p></o:p></span></p>    <p class="MsoNormal"><span style="color: black; font-size: 12pt;">Warm    Regards,</span><br></p>    <p class="MsoNormal"><span style="color: black; fo